package com.example.MockManufacturerWeb;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;@Entity@Data@AllArgsConstructor@NoArgsConstructor@Builderpublic class Product {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @Column(name = "name")    private String name;    @Column(            name = "description",            columnDefinition = "varchar(255) default ''"    )    private String description;    public Product(String name) {        this.name = name;    }}