package com.example.MockManufacturerWeb;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.Set;@Controller@RequestMapping("/api")public class ProductController {    private ProductService productService;    @Autowired    public ProductController(ProductService productService) {        this.productService = productService;    }    @PostMapping("/products")    public ResponseEntity<List<Product>> createProducts(@RequestBody List<Product> productList) {        return productService.createProducts(productList);    }    @GetMapping("/products")    public ResponseEntity<Set<Product>> getProducts(@RequestParam(required = false) String name) {        return productService.getProducts(name);    }    @PutMapping("/products/{id}")    public ResponseEntity<Product> updateProductById(@PathVariable Long id, @RequestBody Product newProduct) {        return productService.updateProductById(id, newProduct);    }    @DeleteMapping("/products/{id}")    public ResponseEntity<String> deleteProductById(@PathVariable Long id) {        return productService.deleteProductById(id);    }}